import utimeimport urequestsimport st7789import tft_typesetfrom machine import Pin, SPITMOMAGENTA = 0xE00ETMOGREY = st7789.color565(128,128,128)timeout = 0paused = Falsedef do_connect():    import network    wlan = network.WLAN(network.STA_IF)    wlan.active(True)    wlan.config(reconnects=3)    if not wlan.isconnected():        print('connecting to network...')        wlan.connect('tmobile', '')        while not wlan.isconnected():            pass        print('network config:', wlan.ifconfig())    return(wlan.ifconfig()[0])def printfortune():    ip = do_connect()    fortune = urequests.get(url='http://hairball.mine.nu/fortune-txt.shtml').text    print(fortune)    TD.clear()    TD.typeset(fortune)    TD.typeset(ip, 7, 7, fg=TMOGREY)def btn1handler(pin):    global timeout    timeout = 0    def btn2handler(pin):    global paused, timeout    paused = not pausedbtn1 = tft_typeset.Button(pin=Pin(0, mode=Pin.IN, pull=Pin.PULL_UP), callback=btn1handler)btn2 = tft_typeset.Button(pin=Pin(35, mode=Pin.IN, pull=Pin.PULL_UP), callback=btn2handler)    def main():    global timeout, paused    TD.clear()    TD.typeset('Connect to WiFi', 0, 0, font=tft_typeset.font2)    TD.typeset(do_connect(), 0, 2)    while True:        if timeout < 1:            TD.typeset("Downloading fortune", 7, 7, fg=TMOGREY)            printfortune()            timeout = 60        if paused:            TD.typeset('||', 1, 7, fg=TMOMAGENTA)        else:            TD.typeset('>>', 1, 7, fg=st7789.GREEN)            TD.typeset("{:2}".format(timeout), 4, 7, fg=TMOGREY)            timeout -= 1        utime.sleep(1)    main()